@app.route('/add_to_cart', methods=['POST'])
@login_required
def add_to_cart():
    if session.get('user_type') == 'admin':
        return jsonify({'success': False, 'message': 'Admins cannot purchase items'})

    data = request.json
    product_id = data.get('product_id')
    quantity = data.get('quantity', 1)
    customization = data.get('customization', {})

    try:
        quantity = int(quantity)
        if quantity <= 0:
            return jsonify({'success': False, 'message': 'Quantity must be a positive integer'})
    except (ValueError, TypeError):
        return jsonify({'success': False, 'message': 'Invalid quantity'})

    product = Product.query.get(product_id)
    if not product:
        return jsonify({'success': False, 'message': 'Product not found'})

    customization['quantity'] = quantity

    if product.category == 'h-frames' and quantity >= 100:
        return jsonify({'success': False, 'message': 'For orders of 100+ pieces, please contact us directly at sales@nationalscaffolding.com'})

    price_data = calculate_price(product, customization)
    if price_data is None:
        return jsonify({'success': False, 'message': 'Cannot calculate price for this configuration'})

    cart = session.get('cart', [])

    cart_item = {
        'product_id': product_id,
        'product_name': product.name,
        'quantity': quantity,
        'customization': customization
    }

    cart.append(cart_item)
    session['cart'] = cart

    return jsonify({'success': True, 'cart_count': len(cart)})

@app.route('/cart')
@login_required
def cart():
    if session.get('user_type') == 'admin':
        return redirect(url_for('admin_scaffoldings'))

    cart_items = session.get('cart', [])

    enriched_cart = []
    total = 0
    total_deposit = 0

    for item in cart_items:
        product = Product.query.get(item['product_id'])
        if product:
            price_data = calculate_price(product, item['customization'])
            if price_data is not None:
                unit_price = price_data['price']
                deposit = price_data['deposit']
                item_total = unit_price * item['quantity']
                item_deposit = deposit * item['quantity'] if deposit > 0 else 0
                total += item_total
                total_deposit += item_deposit

                enriched_item = {
                    'product_name': product.name,
                    'quantity': item['quantity'],
                    'unit_price': unit_price,
                    'deposit': deposit,
                    'item_total': item_total,
                    'item_deposit': item_deposit,
                    'customization': item['customization'],
                    'image_url': product.image_url
                }
                enriched_cart.append(enriched_item)

    return render_template('cart.html', cart_items=enriched_cart, total=total, total_deposit=total_deposit)

@app.route('/remove_from_cart/<int:index>')
@login_required
def remove_from_cart(index):
    cart = session.get('cart', [])
    if 0 <= index < len(cart):
        cart.pop(index)
        session['cart'] = cart

    return redirect(url_for('cart'))

@app.route('/qr_scanner')
@login_required
def qr_scanner():
    if session.get('user_type') == 'admin':
        return redirect(url_for('admin_scaffoldings'))

    cart_items = session.get('cart', [])
    total = 0
    total_deposit = 0

    for item in cart_items:
        product = Product.query.get(item['product_id'])
        if product:
            price_data = calculate_price(product, item['customization'])
            if price_data is not None:
                total += price_data['price'] * item['quantity']
                total_deposit += price_data['deposit'] * item['quantity'] if price_data['deposit'] > 0 else 0

    gst = total * 0.18
    total_with_gst = total + gst + total_deposit

    qr_data = f"upi://pay?pa=nationalscaffolding@phonepe&pn=The National Scaffolding&am={total_with_gst:.2f}&cu=INR"
    qr = qrcode.QRCode(version=1, box_size=10, border=5)
    qr.add_data(qr_data)
    qr.make(fit=True)

    img = qr.make_image(fill_color="black", back_color="white")
    buffered = io.BytesIO()
    img.save(buffered, format="PNG")
    qr_code_base64 = base64.b64encode(buffered.getvalue()).decode()

    return render_template('qr_scanner.html', total=total, gst=gst, total_deposit=total_deposit, total_with_gst=total_with_gst, qr_code=qr_code_base64)

@app.route('/complete_order', methods=['POST'])
@login_required
def complete_order():
    if session.get('user_type') == 'admin':
        return jsonify({'success': False, 'message': 'Admins cannot place orders'})

    cart_items = session.get('cart', [])
    if not cart_items:
        return jsonify({'success': False, 'message': 'Cart is empty'})

    data = request.json or {}
    transaction_id = data.get('transaction_id', '').strip()

    if not transaction_id or len(transaction_id) < 8:
        return jsonify({'success': False, 'message': 'Valid Transaction ID is required (minimum 8 characters)'})

    existing_order = Order.query.filter_by(transaction_id=transaction_id).first()
    if existing_order:
        return jsonify({'success': False, 'message': 'This Transaction ID has already been used. Each purchase requires a unique payment. Please make a new payment and enter the new Transaction ID.'})

    total = 0
    total_deposit = 0

    for item in cart_items:
        product = Product.query.get(item['product_id'])
        if product:
            price_data = calculate_price(product, item['customization'])
            if price_data is not None:
                total += price_data['price'] * item['quantity']
                total_deposit += price_data['deposit'] * item['quantity'] if price_data['deposit'] > 0 else 0

    gst = total * 0.18
    total_with_gst = total + gst + total_deposit

    order = Order(
        user_id=current_user.id, 
        total_price=total_with_gst, 
        status='pending_verification',
        transaction_id=transaction_id,
        amount_paid=0
    )
    db.session.add(order)
    db.session.flush()

    for item in cart_items:
        product = Product.query.get(item['product_id'])
        if product:
            price_data = calculate_price(product, item['customization'])
            if price_data is not None:
                order_item = OrderItem(
                    order_id=order.id,
                    product_id=item['product_id'],
                    product_name=product.name,
                    quantity=item['quantity'],
                    price=price_data['price'],
                    customization=item.get('customization')
                )
                db.session.add(order_item)

    db.session.commit()
    session['cart'] = []

    send_order_confirmation_email(current_user, order, order.order_items)
    send_admin_notification_email(order, current_user, order.order_items)

    return jsonify({'success': True, 'order_id': order.id})

